// 플랫폼 ID에 따른 아이콘 컴포넌트 매핑
const iconComponents: Record<string, React.FC> = {
  instagram: InstagramIcon,
  youtube: YouTubeIcon,
  twitter: TwitterIcon,
  tiktok: TikTokIcon,
  facebook: FacebookIcon,
  threads: ThreadsIcon,
  discord: DiscordIcon,
  twitch: TwitchIcon,
  spotify: SpotifyIcon,
  soundcloud: SoundCloudIcon,
  github: GitHubIcon,
  linkedin: LinkedInIcon,
  naverblog: NaverBlogIcon,
  email: EmailIcon,
};

// 플랫폼별 색상
const platformColors: Record<string, string> = {
  instagram: "#E4405F",
  youtube: "#FF0000",
  twitter: "#000000",
  tiktok: "#000000",
  facebook: "#1877F2",
  threads: "#000000",
  discord: "#5865F2",
  twitch: "#9146FF",
  spotify: "#1DB954",
  soundcloud: "#FF5500",
  github: "#181717",
  linkedin: "#0A66C2",
  naverblog: "#03C75A",
  email: "#EA4335",
};

const sizeClasses = {
  sm: "w-5 h-5",
  md: "w-6 h-6",
  lg: "w-8 h-8",
};

const bgSizeClasses = {
  sm: "w-8 h-8",
  md: "w-10 h-10",
  lg: "w-12 h-12",
};

export function SocialPlatformIcon({
  platform,
  size = "md",
  className,
  showBackground = false,
}: SocialPlatformIconProps) {
  const normalizedPlatform = platform.toLowerCase().replace(/\s/g, "");
  const IconComponent = iconComponents[normalizedPlatform];
  const color = platformColors[normalizedPlatform] || "#666666";

  if (!IconComponent) {
    // 기본 아이콘 (알 수 없는 플랫폼)
    return (
      <div
        className={cn(
          "flex items-center justify-center rounded-full bg-gray-200",
          showBackground ? bgSizeClasses[size] : sizeClasses[size],
          className
        )}
      >
        <span className="text-xs font-bold text-gray-600">
          {platform.charAt(0).toUpperCase()}
        </span>
      </div>
    );
  }

  if (showBackground) {
    return (
      <div
        className={cn(
          "flex items-center justify-center rounded-full transition-transform hover:scale-110",
          bgSizeClasses[size],
          className
        )}
        style={{ backgroundColor: color }}
      >
        <div className={cn(sizeClasses[size], "text-white")}>
          <IconComponent />
        </div>
      </div>
    );
  }

  return (
    <div
      className={cn(sizeClasses[size], "transition-transform hover:scale-110", className)}
      style={{ color }}
    >
      <IconComponent />
    </div>
  );
}

// 플랫폼 이름으로 색상 가져오기
export function getPlatformColor(platform: string): string {
  const normalizedPlatform = platform.toLowerCase().replace(/\s/g, "");
  return platformColors[normalizedPlatform] || "#666666";
}

